sp_widget.client_script:
function() {
	/* widget controller */
	var c = this;

	c.selectItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		c.last_idx = idx;
		$rootScope.noteID = id;
		c.server.get({
			action:'saveIdx'

		});

		$rootScope.$emit('selectNote',id);
	}

	$rootScope.$on('updateNote',function(event,rec){
		console.log("On Notes List");

		c.server.get({
			action: 'updateElement',
			noteID: rec.data.noteID,
			newTitle: rec.data.title,
			last_idx: c.last_idx


		}).then(function(r){
			     console.log(r.data.notes);
		       c.data.notes = r.data.notes;
	})});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
  if(!data.notes)
	      data.notes = [];



	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user',gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while(noteGR.next()){
		var noteObj =  {};
		$sp.getRecordDisplayValues(noteObj,noteGR,'number,title,sys_id');
		if(noteGR.getValue('note')){
				noteObj.note = noteGR.getValue('note').slice(0,20);	
		}else{
			 noteObj.note = "";
		}

		data.notes.push(noteObj);
	}

	if(data.notes && input && input.action == 'updateElement'){


		console.log("updateElement: index is "+input.last_idx);
		console.log("Updating from "+data.notes[input.last_idx]);
		data.notes[input.last_idx].title = input.newTitle;
		console.log("To "+input.newTitle);

	}



})();
